gd();
function f(x,w){
    let res = 0;
    for(let i=0;i<w.length;i++){
        res+=w[i]*x[i];
    }
    return res;
}

function gd() {
    let w = [0, 0];
    let x = [[0, 0], [1, 1], [1, 2], [1, 3], [2, 1], [2, 2], [2, 3]];
    let y = [0, 2, 3, 4, 3, 4, 5];
    let alpha = 0.1;
    for (let t = 0; t < 1000000; t++) {
        for (let i = 0; i < y.length; i++) {
            let j_w = j(w, x[i], y[i]);
            for (let k = 0; k < j_w.length; k++) {
                w[k] = w[k] - alpha * j_w[k];
            }
        }
    }

    console.log(w);
}

function j(w,x,y){
    let j_w = [];
    for(let i=0;i<w.length;i++){
        let tmp = 0;
        for(let j=0;j<w.length;j++){
            tmp+=j!==i?w[j]*x[j]:x[j];
        }
        j_w.push((f(x,w)-y)*tmp);
    }
    return j_w;
}